sys_variable_value.value:
(function execute(inputs, outputs) {

  var approvalList = [];

  if(gs.nil(inputs['entitlement_id'])){
  	outputs['error'] = 'no_input';
    return;
  }

  //Get approval
  var i = 0;
  var gr = new GlideRecord('sys_user');
  gr.addActiveQuery();
  gr.query();
  while(i<inputs['entitlement_id']){
     gr.next();
 	 approvalList.push(gr);
     i++;
  }

  //Set output
  outputs['approver_list'] = approvalList;
  outputs['array_size'] = approvalList.length || 0;

  if(approvalList.length == 0){
    outputs['error'] = 'no_approver';
    return;
  }

  if(outputs['array_size'] > inputs['threshold']){
   outputs['error'] = 'threshold'; 
    return;
  }

})(inputs, outputs);

